import Slider from "react-slick";

import * as S from "./styles";
import imgSlider from "../../images/roommates-sharing-happy-moments-together.jpg";

export const Hero = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <S.HeroWrapper>
      <S.Slider className="curve">
        <S.Button>Find your roommate</S.Button>
        <div>
          <img src={imgSlider} alt="" width="100%" height="100%" />
        </div>
      </S.Slider>
    </S.HeroWrapper>
  );
};
