import styled from "styled-components";

export const HeroWrapper = styled.section`
  width: 100vw;
  height: 100%;
`;

export const Slider = styled.div`
  width: 100%;

  &.curve {
    width: 100%;
    height: 100%;
    top: 0px;
    left: 0px;
    position: absolute;
    background: rgb(250, 250, 250);
    clip-path: polygon(0% 0%, 100% 0, 100% 90%, 51% 100%, 0 90%);
    z-index: 1;
  }
`;
