import L from "leaflet";
import { useState, useRef } from "react";
import { MapContainer, TileLayer, useMapEvents } from "react-leaflet";

import icon from "../../utils/constants";
import { FiPlus } from "react-icons/fi";

import * as S from "./styles";
import { Input, Textarea } from "../Input/";

export const RoommateCreate = () => {
  const formRef = useRef < FormHandles > null;

  const [position, setPosition] = useState({
    latitude: -22.199547,
    longitude: -47.3804479,
  });

  const [images, setImages] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);

  function handleSelectImages(event) {
    if (!event.target.files) {
      return;
    }

    const selectedImages = Array.from(event.target.files);
    setImages(selectedImages);

    const selectedImagesPreview = selectedImages.map((image) => {
      return URL.createObjectURL(image);
    });

    setPreviewImages(selectedImagesPreview);
  }

  function MarkerMap() {
    const map = useMapEvents({
      click: (e) => {
        const { lat, lng } = e.latlng;
        setPosition({
          latitude: lat,
          longitude: lng,
        });
        L.marker([lat, lng], { icon }).addTo(map);
      },
    });
    return null;
  }

  const handleSubmit = (data, { reset }) => {
    console.log(formRef);
    console.log(data);

    reset();
  };

  return (
    <S.RoommatesMapWrapper>
      <S.RoommatesMapContent onSubmit={handleSubmit} ref={formRef}>
        <S.Rooms>
          <S.InputBlock>
            <label htmlFor="title">Titulo</label>
            <Input name="title" />
          </S.InputBlock>

          <S.InputBlock>
            <label htmlFor="description">Descrição</label>
            <Textarea name="description" maxLength={200} />
          </S.InputBlock>

          <S.InputBlock>
            <label htmlFor="price">Preço</label>
            <Input name="price" />
          </S.InputBlock>

          <S.InputBlock>
            <label htmlFor="images">Fotos</label>

            <div className="images-container">
              {previewImages.map((image) => {
                return <img key={image} src={image} alt="" />;
              })}

              <label htmlFor="image[]" className="new-image">
                <FiPlus size={24} color="#15b6d6" />
              </label>
            </div>
            <input
              multiple
              onChange={handleSelectImages}
              type="file"
              id="image[]"
            />
          </S.InputBlock>

          <S.InputBlock>
            <input type="submit" value="Enviar" />
          </S.InputBlock>
        </S.Rooms>

        <S.Map id="map">
          <MapContainer
            zoom={15}
            center={[position.latitude, position.longitude]}
            style={{ width: "100%", height: "100%" }}
          >
            <TileLayer
              url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}
            />
            <MarkerMap />
          </MapContainer>
        </S.Map>
      </S.RoommatesMapContent>
    </S.RoommatesMapWrapper>
  );
};
