import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`

:root {
  --light-blue: rgba(43, 103, 246, 0.16);
  --blue: #2B67F6;
  --white: #F4F7FF;
  --gray: #A5A7A7;
  --black: #333333;
  --green: #27AE60;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  color: white;
  background: #f3f3f3;
}

body,
input,
button,
textarea {
  font: 600 18px Roboto, sans-serif;
}
`;
