import styled from "styled-components";

export const HeaderWrapper = styled.header`
  width: 100vw;
  height: 3.8rem;
  display: flex;
  background: linear-gradient(329.54deg, #fbab7e 0%, #f7ce68 100%);
`;

export const HeaderContent = styled.div`
  width: 100%;
  max-width: 1250px;
  margin: 0 auto;

  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const Logo = styled.div`
  font-size: 1.4em;
  display: flex;
`;
